            ...\rar["\partial^*"]& \widetilde{H}^*(D_k)\rar\dar[equal] & \widetilde{H}^*(V_{k+1})\rar\dar["\sigma_{k+1}^*"] &\widetilde{H}^*(V_k)\rar["\partial^*"]\dar["\sigma_k^*"] & ...\\
